function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
  // server = the created node-opcua server
  // addressSpace = address space of the node-opcua server
  // eventObjects = add event variables here to hold them in memory from this script

  // internal sandbox objects are:
  // node = the compact server node,
  // coreServer = core compact server object for debug and access to NodeOPCUA
  // this.sandboxNodeContext = node context node-red
  // this.sandboxFlowContext = flow context node-red
  // this.sandboxGlobalContext = global context node-red
  // this.sandboxEnv = env variables
  // timeout and interval functions as expected from nodejs

  const opcua = coreServer.choreCompact.opcua;
  const LocalizedText = opcua.LocalizedText;
  const namespace = addressSpace.getOwnNamespace();

  const Variant = opcua.Variant;
  const DataType = opcua.DataType;
  const DataValue = opcua.DataValue;

  var flexServerInternals = this;

  coreServer.debugLog("init dynamic address space");
  const rootFolder = addressSpace.findNode("RootFolder");

  node.warn("construct new address space for OPC UA");

//Deklaration der Struktur
  const myDevice = namespace.addFolder(rootFolder.objects, {
    "browseName": "Device"
  });
  const dynVar = namespace.addFolder(myDevice, { "browseName": "DynamicVariables" });
  const par = namespace.addFolder(myDevice, { "browseName": "Parameters" });
  const perf = namespace.addFolder(myDevice, { "browseName": "Performance" });
  const nameplate = namespace.addFolder(myDevice, { "browseName": "Nameplate" });

//Deklaration der Variablen

const temp1 = namespace.addVariable({
    "organizedBy": dynVar,
    "browseName": "Input: Temperature_1",
    "nodeId": "ns=1;s=T1",
    "dataType": "Double",
    "value": {
          "dataType": DataType.Double,
          "value": 40,
          "min": 30,
          "max": 60 
    }
});


  const ctrlerr1 = namespace.addVariable({
    "organizedBy": dynVar,
    "browseName": "ControllerError_1",
    "nodeId": "ns=1;s=CE1",
    "dataType": "Double",
    "value": {
      "dataType": DataType.Double,
      "value": null,
      "min": -60,
      "max": 60
    }
  });
  const manvar1 = namespace.addVariable({
    "browseName": "ManipulatedVariable_1",
    "dataType": "Double",
    "value": {
      "dataType": DataType.Double,
      "value": null,
      "min": 0,
      "max": 80
    },
    "nodeId": "ns=1;s=MV1"
  });

  const actTemp1 = namespace.addVariable({
    "browseName": "Output: actualTemperature_1",
    "dataType": "Double",
    "value": {
      "dataType": DataType.Double,
      "value": null,
      "min": 30,
      "max": 60
    },
    "nodeId": "ns=1;s=H1"
  });

  const led = namespace.addVariable({
    "browseName": "LED",
    "dataType": "Boolean",
    "value": {
      "dataType": DataType.Boolean,
      "value": false
    },
    "nodeId": "ns=1;s=led"
  });

  const integralKi = namespace.addVariable({
    "browseName": "Integral: Ki",
    "dataType": "Double",
    "value": {
      "dataType": DataType.Double,
      "value": 0.0088
    },
    "nodeId": "ns=1;s=ki"
  });

  const proportionalKp = namespace.addVariable({
    "browseName": "Proportional: Kp",
    "dataType": "Double",
    "value": {
      "dataType": DataType.Double,
      "value": 1.6
    },
    "nodeId": "ns=1;s=kp"
  });

  const temperature1HighLimit = namespace.addVariable({
    "browseName": "Temperature_1_High_Limit",
    "dataType": "Double",
    "value": {
      "dataType": DataType.Double,
      "value": 60
    },
    "nodeId": "ns=1;s=T1_limit"
  });

  const manufacturerName = namespace.addVariable({
    "browseName": "ManufacturerName",
    "dataType": "String",
    "value": {
      "dataType": DataType.String,
      "value": "Byu Prism"
    },
    "nodeId": "ns=1;s=manufacturer_name"
  });

  const manufacturerProductDesignation = namespace.addVariable({
    "browseName": "ManufacturerProductDesignation",
    "dataType": "String",
    "value": {
      "dataType": DataType.String,
      "value": "Arduino Temperature Control Lab"
    },
    "nodeId": "ns=1;s=manufacturer_product_designation"
  });

  const manufacturerProductFamily = namespace.addVariable({
    "browseName": "ManufacturerProductFamily",
    "dataType": "String",
    "value": {
      "dataType": DataType.String,
      "value": "Educational Equipment"
    },
    "nodeId": "ns=1;s=manufacturer_product_family"
  });

  const serialNumber = namespace.addVariable({
    "browseName": "SerialNumber",
    "dataType": "String",
    "value": {
      "dataType": DataType.String,
      "value": "F7DH4HJ"
    },
    "nodeId": "ns=1;s=serial_number"
  });

  const yearOfConstruction = namespace.addVariable({
    "browseName": "YearOfConstruction",
    "dataType": "String",
    "value": {
      "dataType": DataType.String,
      "value": "2020"
    },
    "nodeId": "ns=1;s=year_of_construction"
  });

  const address = namespace.addVariable({
    "browseName": "Address",
    "dataType": "String",
    "value": {
      "dataType": DataType.String,
      "value": "Brigham Young University, Provo, UT 84602"
    },
    "nodeId": "ns=1;s=address"
  });




  //------------------------------------------------------------------------------
  // Add a view
  //------------------------------------------------------------------------------

  coreServer.debugLog("create dynamic address space done");
  node.warn("construction of new address space for OPC UA done");

  done();
}

